import React from 'react';
import Avatar from '../Avatar';
import css from './changeUser.module.css';

export default function ChangeUser(props) {
  function onClickBatman() {
    return props.onClick('batman');
  }

  const isBatmanActive = props.user === 'batman';
  const isSupermanActive = props.user === 'superman';
  const isWonderWomanActive = props.user === 'wonderWoman';

  return (
    <div>
      <h6>Visualizar Timeline com:</h6>
      <div>
        {props.bestFriends.map((bestFriend, index) => {
          return (
            <div
              onClick={onClickBatman}
              className={isBatmanActive ? css.active : css.inactive}
            >
              <Avatar user={props.user} />
            </div>
          );
        })}
      </div>
    </div>
  );
}
