import React from 'react';
import Avatar from '../Avatar/Avatar';

export default function PostComment(props) {
  return (
    <div>
      <div>
        <Avatar user={props.comment.user} avatarSize="small" />
      </div>

      <div>
        <p>{props.comment.comment}</p>
      </div>
    </div>
  );
}
