import React from 'react';
import { v4 as uuidv4 } from 'uuid';

import PostComment from './PostComment';
import Likes from './Likes';
import CurrentUserComment from './CurrentUserComment';

export default function MoviePost(props) {
  return (
    <div>
      <div>
        <img src={props.post.picture} />
      </div>

      <div>
        <div>
          <CurrentUserComment user={props.user} comment={props.post.title} />
        </div>

        <div>
          <Likes likes={props.likes} />
        </div>

        <div>
          {props.comments.map((comment, index) => (
            <PostComment
              key={index}
              comment={comment}
              user={props.comment.user}
            />
          ))}
        </div>

        <div>
          <textarea value="" cols="30" rows="10">
            Add a comment
          </textarea>
        </div>
      </div>
    </div>
  );
}
