import React from 'react';
import PostDescription from './PostDescription';
import Likes from './Likes';
import CurrentUserComment from './CurrentUserComment';

export default function MoviePost(props) {
  useEffect(() => {
    const getComments = async () => {
      const res = await fetch('http://localhost:3001/comments');
      const json = await res.json();

      setComments(json);
    };

    getComments();
  }, []);

  useEffect(() => {
    const getLikes = async () => {
      const res = await fetch('http://localhost:3001/likes');
      const json = await res.json();

      setBestFriends(json);
    };

    getLikes();
  }, []);

  return (
    <div>
      <div>
        <img src="" alt="post" />
      </div>

      <div>
        <div>
          <CurrentUserComment
            activeUser={props.activeUser}
            comment={props.comment}
          />
        </div>

        <div>
          <Likes />
        </div>

        <div>
          <PostDescription user={props.user} comment={props.comment} />
        </div>

        <div>
          <PostDescription user={props.user} comment={props.comment} />
        </div>

        <div>
          <textarea value="" cols="30" rows="10">
            Add a comment
          </textarea>
        </div>
      </div>
    </div>
  );
}
