import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';

import Header from './Header';
import CurrentUserInfo from './CurrentUserInfo';
import ChangeUser from './ChangeUser';
import Timeline from './Timeline';

export default function Herosgram() {
  const [activeUser, setActiveUser] = useState('superman');
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState([]);
  const [likes, setLikes] = useState([]);
  const [bestFriends, setBestFriends] = useState([]);

  useEffect(() => {
    const getPosts = async () => {
      const res = await fetch('http://localhost:3001/posts?user=superman');
      const json = await res.json();

      setPosts(json);
    };

    getPosts();
  }, []);

  useEffect(() => {
    const getComments = async () => {
      const res = await fetch('http://localhost:3001/comments');
      const json = await res.json();

      setComments(json);
    };

    getComments();
  }, []);

  useEffect(() => {
    const getLikes = async () => {
      const res = await fetch('http://localhost:3001/likes');
      const json = await res.json();

      setBestFriends(json);
    };

    getLikes();
  }, []);

  useEffect(() => {
    const getBestFriends = async () => {
      const res = await fetch('http://localhost:3001/bestFriends');
      const json = await res.json();

      setLikes(json);
    };

    getBestFriends();
  }, []);

  function handleOnChangeUser(clickedUser) {
    console.log({ clickedUser });
  }

  const users = [
    {
      user: 'batman',
      isActive: false,
      onClick: () => handleOnChangeUser('batman'),
    },
    {
      user: 'superman',
      isActive: true,
      onClick: () => handleOnChangeUser('superman'),
    },
    {
      user: 'batman',
      isActive: false,
      onClick: () => handleOnChangeUser('batman'),
    },
  ];

  return (
    <div>
      <Header />

      <div>
        <div>
          <CurrentUserInfo user="superman" />
        </div>

        <div>
          <ChangeUser users={users} onClick={handleOnChangeUser} />
        </div>
      </div>

      <Timeline />
    </div>
  );
}
