import React from 'react';

export default function AvatarImage(props) {
  const alt = props.alt || 'Hero Image';

  if (!props.user) {
    return <img src="/img/default.png" />;
  }

  return <img src={`/img/${props.user}.png`} alt={alt} />;
}
