import React from 'react';
import AvatarImage from '../AvatarImage/AvatarImage';
import HeroName from '../HeroName/HeroName';
import css from './avatar.module.css';

export default function Avatar(props) {
  const alignmentContainer = css[props.alignment] || css['left'];
  const marginLeft = css[props.marginLeft];

  return (
    <div className={`${alignmentContainer} ${marginLeft}`}>
      <div>
        <AvatarImage user={props.user} size={props.avatarSize} />
      </div>

      <div>
        <HeroName
          name={props.user}
          margin={props.margin}
          fontSize={props.fontSize}
        />
      </div>
    </div>
  );
}
