import React, { useEffect, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import PostDescription from './PostDescription';
import Likes from './Likes';
import CurrentUserComment from './CurrentUserComment';

export default function MoviePost(props) {
  const [comments, setComments] = useState([]);
  const [likes, setLikes] = useState([]);

  useEffect(() => {
    const getComments = async () => {
      const res = await fetch(
        `http://localhost:3001/comments?postId=${props.post.id}`
      );
      const json = await res.json();

      setComments(json);
    };

    getComments();
  }, []);

  useEffect(() => {
    const getLikes = async () => {
      const res = await fetch(
        `http://localhost:3001/likes?postId=${props.post.id}`
      );
      const json = await res.json();

      setLikes(json);
    };

    getLikes();
  }, []);

  return (
    <div>
      <div>
        <img src={props.post.picture} alt="post" />
      </div>

      <div>
        <div>
          <CurrentUserComment
            user={props.post.user}
            comment={props.post.title}
          />
        </div>

        <div>
          <Likes likes={likes} />
        </div>

        <div>
          {comments.map((comment, index) => (
            <PostDescription key={index} comment={comment} />
          ))}
        </div>

        <div>
          <textarea value="" cols="30" rows="10">
            Add a comment
          </textarea>
        </div>
      </div>
    </div>
  );
}
